import React from 'react';
import { Play } from 'lucide-react';

const CodeEditor = ({ code, setCode, onRun, isRunning }) => {
  return (
    <div className="w-1/2 p-4">
      <div className="bg-white rounded-lg shadow h-full flex flex-col">
        <div className="flex justify-between items-center p-4 border-b">
          <h3 className="text-lg font-semibold text-gray-700">Code Editor</h3>
          <button
            onClick={onRun}
            disabled={isRunning}
            className="flex items-center gap-2 px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 disabled:bg-gray-400 transition duration-200"
          >
            <Play size={16} />
            {isRunning ? 'Running...' : 'Run'}
          </button>
        </div>
        <textarea
          value={code}
          onChange={(e) => setCode(e.target.value)}
          className="flex-1 p-4 font-mono text-sm resize-none outline-none"
          placeholder="Write your YantraBhashi code here..."
        />
      </div>
    </div>
  );
};

export default CodeEditor;
