import React, { useState } from 'react';
import Header from '../common/Header';
import CodeEditor from './CodeEditor';
import OutputPanel from './OutputPanel';
import YantraBhashiValidator from '../../services/validator';
import YantraBhashiInterpreter from '../../services/interpreter';
import mockAPI from '../../services/mockAPI';
import '../../styles/components.css';

const CompilerInterface = ({ user, onLogout }) => {
  const [code, setCode] = useState(`# Hello World program
PADAM message:VARTTAI = "Hello World";
CHATIMPU(message);`);
  const [output, setOutput] = useState('');
  const [isRunning, setIsRunning] = useState(false);

  const runCode = async () => {
    setIsRunning(true);

    // First validate the code
    const validator = new YantraBhashiValidator();
    const validationResult = validator.validate(code);

    let outputText = '';
    let status = 'success';
    let errors = [];

    if (!validationResult.isValid) {
      status = 'error';
      errors = validationResult.errors;
      outputText = 'Compilation Errors:\n' + validationResult.errors.map(err => `Line ${err.line}: ${err.message}`).join('\n');
    } else {
      outputText = 'Code compiled successfully!\nOutput:\n';

      // Run the interpreter
      const interpreter = new YantraBhashiInterpreter();
      const executionResult = interpreter.run(code, []); // Empty array for inputs, you can modify this as needed

      if (executionResult.success) {
        outputText += executionResult.output || '(No output)';
      } else {
        status = 'runtime_error';
        outputText += `Runtime Error: ${executionResult.error}`;
        if (executionResult.output) {
          outputText += `\n\nOutput before error:\n${executionResult.output}`;
        }
      }
    }

    setOutput(outputText);

    try {
      // Call backend submit API
      await fetch('http://localhost:4000/submit', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: user.userId,
          code,
          status,
          output: status === 'success' ? outputText : '',
          errors,
        }),
      });
    } catch (error) {
      console.error('Error submitting code:', error);
    }

    setIsRunning(false);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Header
        title="YantraBhashi Compiler"
        user={user}
        onLogout={onLogout}
      />
      <div className="flex h-screen">
        <CodeEditor
          code={code}
          setCode={setCode}
          onRun={runCode}
          isRunning={isRunning}
        />
        <OutputPanel output={output} />
      </div>
    </div>
  );
};

export default CompilerInterface;
