import React, { useState } from 'react';
import mockAPI from '../../services/mockAPI';
import { DEMO_CREDENTIALS } from '../../utils/constants';
import '../styles/components.css';

const LoginForm = ({ onLogin, onSwitchToSignup }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    if (!username || !password) {
      setError('Username and password are required');
      return;
    }
    try {
      const user = await mockAPI.login(username, password);
      onLogin(user);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-purple-900 flex items-center justify-center">
      <div className="bg-white rounded-lg shadow-xl p-8 w-96">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">Login</h2>
        {error && <div className="bg-red-100 text-red-700 p-3 rounded mb-4">{error}</div>}

        <div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-bold mb-2">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            />
          </div>
          <button
            onClick={handleSubmit}
            className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition duration-200"
          >
            Login
          </button>
        </div>

        <p className="text-center mt-4">
          Don't have an account?{' '}
          <button onClick={onSwitchToSignup} className="text-blue-500 hover:underline">
            Sign Up
          </button>
        </p>

        <div className="mt-6 p-4 bg-gray-100 rounded">
          <p className="text-sm text-gray-600 mb-2">Demo Credentials:</p>
          <p className="text-xs">Admin: {DEMO_CREDENTIALS.admin.username} / {DEMO_CREDENTIALS.admin.password}</p>
          <p className="text-xs">Student: {DEMO_CREDENTIALS.student.username} / {DEMO_CREDENTIALS.student.password}</p>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
